import React, { Component } from 'react'
import Activities from './Activities';
import Comments from './Comments';
import { Button } from 'reactstrap';
import { Alert } from 'react-bootstrap'

class ViewAll extends Component {

    constructor(props) {
        super(props);
        this.state = {
            activities: '',
            show: true
        }
        this.alertDismissible = this.alertDismissible.bind(this)
    }

    alertDismissible = (e) => {
        this.setState(prevState => ({
            show: !prevState.show
        }));
    }

    render() {
        const { itinerary } = this.props
        return (
            <div className="viewAll">

                <Button color="tranparent" id="show" onClick={this.handleClick} >

                    {this.state.showContent ? 'View All' : 'Close'}  </Button>

                <Alert show={this.state.show} variant="success">
                    <Alert.Heading>How's it going?!</Alert.Heading>
                    <Activities activities={itinerary.activities} />
                    <Comments itineraryId={itinerary._id} />
                    <hr />
                    <div className="d-flex justify-content-end">
                        <Button onClick={this.alertDismissible} variant="outline-success">
                            Close me ya'll!
         </Button>
                    </div>
                </Alert>

                {!this.state.show && <Button onClick={this.alertDismissible}>Show Alert</Button>}




            </div>
        )
    }
}

export default ViewAll;
