import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchComments } from '../store/actions/commentActions'

class CommentsForm extends Component {
    constructor(props) {
        super(props);
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.prevent.default();
        let { itineraryId } = this.props
        this.props.fetchComments(itineraryId)
    }

    render() {
        return (
            <div classname="commentForm-container">
                <form>
                    <input type="text" id="comment" placeHolder="Your comment" onChange={this.handleChange} />
                    <button type="submit" onSubmit={this.handleSubmit}>></button>
                </form>

            </div>
        )
    }
}

export default CommentsForm;
