import React, { Component, useState } from 'react';
// import { connect } from 'react-redux'
// import userIcon from '../images/userIcon.png'
// import { Link } from 'react-router-dom';
import { Collapse, Button, CardBody, Card } from 'reactstrap';
// import Comments from './Comments';


class Activities extends Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {

        const [collapse, setCollapse] = useState(false);
        const [status, setStatus] = useState('View All');

        const onEntering = () => setStatus('Close');

        const onExited = () => setStatus('View All')

        const toggle = () => setCollapse(!collapse);


        const { activities } = this.props
        // activities is a props(and not state) because it's passed
        // from the itineraries component (line 62)

        console.log(activities)


        return (
            <div className="activities" >
                <Button onClick={toggle}>^ {status} ^</Button>
                <Collapse
                    isOpen={collapse}
                    onEntering={onEntering}
                    onExited={onExited}

                >
                    {activities.map((activity) =>

                        <Card bsclass='custom-card' key={activity._id}>
                            <CardBody> {activity.title} </CardBody>
                        </Card>
                    )}
                </Collapse>


            </div>
        )

    }


}


export default Activities
