import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchCommentPost } from '../store/actions/commenPostActions'

class CommentsPost extends Component {
    constructor(props) {
        super(props);
        this.state = {
            comment: ''
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.prevent.default();
        let { itineraryId } = this.props
        this.props.fetchCommentPost(itineraryId)
    }



    render() {
        return (
            <div className="commentForm-container">
                <form>
                    <input type="text" id="comment" placeholder="Your comment" onChange={this.handleChange} />
                    <button type="submit" onSubmit={this.handleSubmit}>></button>
                </form>

            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        commentsPost: state.commentsPost.commentsPost
    }
}

const mapDispatchProps = dispatch => {
    return {
        fetchCommentPost: (id) => dispatch(fetchCommentPost(id))
    }
}

export default connect(
    mapStateToProps,
    mapDispatchProps
)(CommentsPost);
