import React, { Component, useState } from 'react';
import { Collapse, Button, CardBody, Card } from 'reactstrap';
import Activities from './Activities';
import Comments from './Comments';

class ActivitiesCard extends Component {
    render() {
        const { activitiesCard } = this.props;
        const [collapse, setCollapse] = useState(false);
        const [status, setStatus] = useState('View All');

        const onEntering = () => setStatus('Close');

        const onExited = () => setStatus('View All')


        return (
            <div className="activities-card">
                <Button onClick={toggle}>^ {status} ^</Button>
                <Collapse
                    isOpen={collapse}
                    onEntering={onEntering}
                    onExited={onExited}
                >
                    <Card>
                        <CardBody>

                            <Activities activities={itinerary.activities} />

                            <Comments itineraryId={itinerary._id} />

                        </CardBody>
                    </Card>

                </Collapse>

            </div>
        )
    }
}

export default ActivitiesCard;
